\t","\t\t\tfunction StartSlideshow() {\t\t// Initiated when image DIV is clicked\t\t","\t\t\t\t","\t\t\t\tif (slideshowStart == 0) {\t// Prevent from starting the slideshow again if flag is not set to zero","\t\t\t\t\t","\t\t\t\t\tslideshowStart = 1;   \t\t// If not yet started, update the flag and fade out the intro image and begin to show the list","\t\t\t\t\t","\t\t\t\t\t$( '#slideshow' ).fadeOut( 'slow', function() {","\t\t\t\t\t\t// If page width < 893 hide, else","\t\t\t\t\t\tif ( $(window).width() < 992 ) {","\t\t\t\t\t\t\t$( '#slideshow' ).remove();  ","\t\t\t\t\t\t} else {","\t\t\t\t\t\t\t$( '#slideshow' ).appendTo( '#contentRight' );","\t\t\t\t\t\t}","\t\t\t\t\t});","\t\t\t\t","\t\t\t\t\tHighlightList();","\t\t\t\t}","\t\t\t}","\t","\t\t\tfunction HighlightList() {","\t\t\t\t//Fade in the list","\t\t\t\t$( '#jqueryList' ).fadeIn( 'slow' );","\t\t\t\t//Update CSS alignment","\t\t\t\t// $( '#contentRight' ).css( 'margin-right', '3%' );","","\t\t\t\tvar timeOuts = new Array();","\t\t\t\tvar count = 0; //","\t  \t  var eT=500; //Delay time for change in ms","\t\t\t\tinitLoop();  //Begin the slideshow","\t\t\t\t","\t\t\t\tfunction initLoop() {","\t\t\t\t\t//HIGHLIGHT the list item at the passed index for the set time","\t\t\t\t\t$( 'li[class^=\"listItem\"]' ).each(function(index) {","\t\t\t\t\t\twindow.setTimeout(","\t\t\t\t\t\tfunction() {","\t\t\t\t\t\t\thighlight(index);","\t\t\t\t\t\t}, ","\t\t\t\t\t\tindex*eT);","\t\t\t\t\t\t","\t\t\t\t\t\tconsole.log(\"highlight index\", index)","\t\t\t\t\t});\t\t\t\t","\t\t\t\t\t","\t\t\t\t\t//UN-HIGHLIGHT the list item at the passed index for the set time","\t\t\t\t\t$( 'li[class^=\"listItem\"]' ).each(function(index) {","\t\t\t\t\t\twindow.setTimeout(","\t\t\t\t\t\tfunction() {","\t\t\t\t\t\t\tunHighlight(index);","\t\t\t\t\t\t}, ","\t\t\t\t\t\t(index + 1)*eT);\t\t\t\t\t\t\t\t","\t\t\t\t\t","\t\t\t\t\t\tconsole.log(\"unhighlight index\", index)","\t\t\t\t\t});","\t\t\t\t","\t\t\t\t\t","\t\t\t\t\t","\t\t\t\t}","\t\t\t\t\t\t\t","\t\t\t\t","\t\t\t\t","\t\t\t\t","\t\t\t\t//Update the background/border/text color for the currently active list item using the passed index + give it a shadow and some IE special handling","\t\t\t\tfunction highlight(index) {","\t\t\t\t\tvar listIndex = '.listItem' + index;","\t\t\t\t\t$( listIndex ).css( 'background-color', 'black' );","\t\t\t\t\t$( listIndex ).css( 'border', '3px double #ffdd00' );","\t\t\t\t\t$( listIndex ).css( 'color', 'white' );","\t\t\t\t\t// $( '#contentLeft' ).find(listIndex).addClass( 'iehighlight' );","\t\t\t\t\t// $( '#contentLeft' ).find(listIndex).addClass( 'shadow' );","\t","\t\t\t\t\t//Fade out the previous picture and show the next one to correspond with the current list item","\t\t\t\t\t$( '#slideshow' ).fadeOut( 'fast', function(){","\t\t\t\t\t\t$( '#slideshow' ).css( 'background-image', 'url(images/slideshow/' + (index +1) +'.jpg)' );","\t\t\t\t\t\t// $( '#slideshow' )'background-size', '460px 250px' );","\t\t\t\t\t\t$( '#slideshow' ).css( 'background-repeat', 'no-repeat' );","\t\t\t\t\t\t$( '#slideshow' ).fadeIn( 'fast' );","\t\t\t\t\t\tif (index == 0) {$( '#contentRight' ).fadeIn( 'slow' );}","\t\t\t\t\t});","\t\t\t\t\t","\t\t\t\t\t","\t\t\t\t\t","\t\t\t\t\t","\t\t\t\t\t//Make the active item stand out a bit more with some alignment CSS tweaks","\t\t\t\t\t// $( listIndex ).css( 'width', '470px' );","\t\t\t\t\t// $( listIndex ).css( 'margin-left', '-5px' );","\t\t\t\t\t// $( listIndex ).css( 'padding', '21px' );","\t\t\t\t\t","\t\t\t\t}","\t\t\t\t","\t\t\t\t//Remove all the highlighting changes for the previously active item","\t\t\t\tfunction unHighlight(index) {","\t\t\t\t\tvar listIndex = '.listItem' + index;","\t\t\t\t\t$( '#contentLeft' ).find(listIndex).removeClass( 'iehighlight' );","\t\t\t\t\t$( '#contentLeft' ).find(listIndex).removeClass( 'shadow' );","\t\t\t\t\t$( listIndex ).css( 'border', '1px solid black' );","\t\t\t\t\t$( listIndex ).css( 'color', '#dddddd' );","\t\t\t\t\t$( listIndex ).css( 'background-